// frontend/src/App.js
import React, { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';
import './App.css';
// At the top of App.js, add this import:
import { FaUpload } from 'react-icons/fa';
import { FaFilePdf } from 'react-icons/fa';
import { FaTrash } from 'react-icons/fa';

import * as pdfjsLib from 'pdfjs-dist';
import { getDocument } from 'pdfjs-dist';
pdfjsLib.GlobalWorkerOptions.workerSrc = "https://cdn.jsdelivr.net/npm/pdfjs-dist@4.9.155/build/pdf.worker.min.mjs"; 


const supabase = createClient(
  process.env.REACT_APP_SUPABASE_URL,
  process.env.REACT_APP_SUPABASE_ANON_KEY
);

function App() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [selectedDoc, setSelectedDoc] = useState(null);
  const [query, setQuery] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState({});
  const [isHistoryLoading, setIsHistoryLoading] = useState(false);

  useEffect(() => {
    if (selectedDoc) {
      fetchChatHistory(selectedDoc.id);
    }
  }, [selectedDoc]);

  const clearChat = async () => {
    if (!selectedDoc) return;

    try {
      // Delete from database
      const { error } = await supabase
        .from('chat_history')
        .delete()
        .eq('document_id', selectedDoc.id);

      if (error) throw error;

      // Clear local state
      setChatHistory([]);
    } catch (error) {
      console.error('Error clearing chat:', error);
      alert('Error clearing chat history');
    }
  };

  // Add this function to fetch chat history
  const fetchChatHistory = async (documentId) => {
  setIsHistoryLoading(true);
  try {
    const { data, error } = await supabase
      .from('chat_history')
      .select('*')
      .eq('document_id', documentId)
      .order('created_at', { ascending: true });

    if (error) throw error;
    setChatHistory(data || []);
  } catch (error) {
    console.error('Error fetching chat history:', error);
  } finally {
    setIsHistoryLoading(false);
  }
};

  const handleDocumentSelect = async (doc) => {
  setSelectedDoc(doc);
  await fetchChatHistory(doc.id);
};


const handleDelete = async (doc) => {
  try {
    // Delete from storage first
    const filename = doc.file_url.split('/').pop();
    const { error: storageError } = await supabase.storage
      .from('pdfs')
      .remove([filename]);

    if (storageError) throw storageError;

    // Delete document (will cascade delete chunks)
    const { error: deleteError } = await supabase
      .from('documents')
      .delete()
      .eq('id', doc.id);

    if (deleteError) throw deleteError;

    // Refresh documents list
    fetchDocuments();
  } catch (error) {
    console.error('Delete error:', error);
    alert('Error deleting document');
  }
};


  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    const { data } = await supabase.from('documents').select('*');
    setDocuments(data || []);
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    const validFiles = files.filter(file => {
      const isValidType = file.type === 'application/pdf';
      const isValidSize = file.size <= 10 * 1024 * 1024; // 10MB
      if (!isValidType) alert(`${file.name} is not a PDF file`);
      if (!isValidSize) alert(`${file.name} exceeds 10MB size limit`);
      return isValidType && isValidSize;
    });
    setSelectedFiles(validFiles);
  };

  // In App.js, modify the fetch call in handleUpload:
  const handleUpload = async () => {
    if (!selectedFiles.length) return;

    setLoading(true);
    try {
      for (const file of selectedFiles) {
        setUploadProgress(prev => ({ ...prev, [file.name]: 0 }));

        // Read PDF first to get page count
        const arrayBuffer = await file.arrayBuffer();
        const pdf = await getDocument({ data: arrayBuffer }).promise;
        const totalPages = pdf.numPages;  // Get total pages

        // Upload to storage
        const { data: uploadData, error: uploadError } = await supabase.storage
          .from('pdfs')
          .upload(`${Date.now()}_${file.name}`, file);

        if (uploadError) throw uploadError;

        const { data: { publicUrl } } = supabase.storage
          .from('pdfs')
          .getPublicUrl(uploadData.path);

        // Create document record with page count
        const { data: docData, error: docError } = await supabase
          .from('documents')
          .insert({
            filename: file.name,
            file_url: publicUrl,
            total_pages: totalPages  // Add total pages
          })
          .select()
          .single();

        if (docError) throw docError;

        // Process PDF content
        let fullText = [];
        for (let i = 1; i <= totalPages; i++) {
          const page = await pdf.getPage(i);
          const textContent = await page.getTextContent();
          const pageText = textContent.items.map(item => item.str).join(' ');

          fullText.push({
            text: pageText,
            pageNumber: i
          });

          setUploadProgress(prev => ({
            ...prev,
            [file.name]: (i / totalPages) * 50
          }));
        }

        // Process with edge function
        const response = await fetch(
          `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-pdf`,
          {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`
            },
            body: JSON.stringify({
              documentId: docData.id,
              pages: fullText
            })
          }
        );

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Failed to process ${file.name}: ${errorText}`);
        }

        setUploadProgress(prev => ({ ...prev, [file.name]: 100 }));
      }

      // Clear selected files and refresh document list
      setSelectedFiles([]);
      setUploadProgress({});
      fetchDocuments();
    } catch (error) {
      console.error('Upload error:', error);
      alert('Error uploading files: ' + error.message);
    } finally {
      setLoading(false);
    }
   };





  // Modify handleQuerySubmit to store chat history
  const handleQuerySubmit = async (e) => {
  e.preventDefault();
  if (!query.trim() || !selectedDoc) return;

  setLoading(true);
  try {
    const response = await fetch(
      `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-query`,
      {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`
        },
        body: JSON.stringify({
          query: query.trim(),
          documentId: selectedDoc.id,
          chatHistory: chatHistory
        })
      }
    );

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error('Failed to process query: ' + errorText);
    }

    const result = await response.json();

    // Create new chat entry
    const newEntry = {
      document_id: selectedDoc.id,
      query: query.trim(),
      response: result.answer,
      context: result.context
    };

    // Store in database
    const { error: insertError } = await supabase
      .from('chat_history')
      .insert(newEntry);

    if (insertError) throw insertError;

    // Update local state
    await fetchChatHistory(selectedDoc.id);
    setQuery('');
  } catch (error) {
    console.error('Query error:', error);
    alert(error.message);
  } finally {
    setLoading(false);
  }
};



return (
  <div className="app-container">
    <div className="sidebar">
      <div className="upload-section">
        <h2>PDF Documents</h2>
        <div className="upload-controls">
          <label className="upload-button">
            <input
              type="file"
              accept=".pdf"
              multiple
              onChange={handleFileChange}
              disabled={loading}
              className="hidden"
            />
            <FaUpload /> Choose Files
          </label>
          <button 
            className="upload-submit"
            onClick={handleUpload}
            disabled={!selectedFiles.length || loading}
          >
            Upload {selectedFiles.length ? `(${selectedFiles.length})` : ''}
          </button>
        </div>
        {Object.entries(uploadProgress).map(([filename, progress]) => (
          <div key={filename} className="upload-progress">
            <span className="filename">{filename}</span>
            <div className="progress-bar">
              <div 
                className="progress" 
                style={{ width: `${progress}%` }}
              />
            </div>
          </div>
        ))}
      </div>

      <div className="documents-list">
        {documents.map(doc => (
          <div
            key={doc.id}
            className={`document-item ${selectedDoc?.id === doc.id ? 'selected' : ''}`}
          >
            <div 
              className="document-info"
              onClick={() => handleDocumentSelect(doc)}
            >
              <FaFilePdf className="doc-icon" />
              <span className="doc-name">{doc.filename}</span>
            </div>
            <button 
              className="delete-button"
              onClick={() => handleDelete(doc)}
            >
              <FaTrash />
            </button>
          </div>
        ))}
      </div>
    </div>

    <div className="chat-container">
      <div className="chat-header">
        {selectedDoc ? (
          <div className="chat-header-content">
            <h2>Chat about: {selectedDoc.filename}</h2>
            {chatHistory.length > 0 && (
              <button 
                className="clear-chat-button"
                onClick={clearChat}
              >
                <FaTrash /> Clear Chat
              </button>
            )}
          </div>
        ) : (
          <h2>Select a document to start chatting</h2>
        )}
      </div>

      <div className="chat-messages">
        {isHistoryLoading ? (
          <div className="loading">Loading chat history...</div>
        ) : (
          chatHistory.map((chat, index) => (
            <div key={index} className="chat-entry">
              <div className="user-message">
                <div className="message-content">
                  <strong>You:</strong> {chat.query}
                </div>
              </div>
              <div className="ai-message">
                <div className="message-content">
                  <strong>AI:</strong> {chat.response}
                </div>
                {chat.context && (
                  <div className="context-info">
                    <small>Source Context:</small>
                    <div className="context-content">
                      {chat.context.map((ctx, idx) => (
                        <div key={idx} className="context-item">
                          <p>{ctx.content}</p>
                          <button
                            className="page-link"
                            onClick={() => openPdfPage(selectedDoc.file_url, ctx.page_number)}
                          >
                            Page {ctx.page_number}
                          </button>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            </div>
          ))
        )}
      </div>

      <form onSubmit={handleQuerySubmit} className="chat-input">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder={selectedDoc ? "Ask a question..." : "Select a document first"}
          disabled={!selectedDoc || loading}
        />
        <button 
          type="submit" 
          disabled={!selectedDoc || loading || !query.trim()}
        >
          Send
        </button>
      </form>
    </div>
  </div>
);
}

export default App;
