// frontend/src/App.js
import React, { useState, useEffect, useRef } from "react";
import { createClient } from "@supabase/supabase-js";
import "./App.css";
import PDFViewer from "./components/PDFViewer";
// At the top of App.js, add this import:
import { FaUpload } from "react-icons/fa";
import { FaFilePdf } from "react-icons/fa";
import { FaTrash } from "react-icons/fa";

import * as pdfjsLib from "pdfjs-dist";
import { getDocument } from "pdfjs-dist";
import MessageFormatter from './components/MessageFormatter';
import ChatHistory from "./components/ChatHistory";
pdfjsLib.GlobalWorkerOptions.workerSrc =
  "https://cdn.jsdelivr.net/npm/pdfjs-dist@4.9.155/build/pdf.worker.min.mjs";

const supabase = createClient(
  process.env.REACT_APP_SUPABASE_URL,
  process.env.REACT_APP_SUPABASE_ANON_KEY
);

function App() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [selectedDoc, setSelectedDoc] = useState(null);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [query, setQuery] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState({});
  const [isHistoryLoading, setIsHistoryLoading] = useState(false);
  const latestMessageRef = useRef(null);

  const renderChatMessage = (chat) => (
    <div className="message ai-message align-self-start">
      <div className="message-content p-3 rounded-3">
        <MessageFormatter 
          content={chat.response}
          context={chat.context}
          selectedDocs={selectedDocs}
          openPdfPage={openPdfPage}
        />
      </div>
    </div>
  );

  useEffect(() => {
    if (latestMessageRef.current) {
      latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [chatHistory]);

  useEffect(() => {
    if (selectedDoc) {
      fetchChatHistory(selectedDoc.id);
    }
  }, [selectedDoc]);

  // Add this function to handle multiple document selection
const handleDocumentSelect = (doc) => {
  setSelectedDocs(prev => {
    const isSelected = prev.some(d => d.id === doc.id);
    if (isSelected) {
      return prev.filter(d => d.id !== doc.id);
    } else {
      return [...prev, doc];
    }
  });
};


  // Add these two new helper functions here:


  const clearChat = async () => {
    if (!selectedDoc) return;

    try {
      // Delete from database
      const { error } = await supabase
        .from("chat_history")
        .delete()
        .eq("document_id", selectedDoc.id);

      if (error) throw error;

      // Clear local state
      setChatHistory([]);
    } catch (error) {
      console.error("Error clearing chat:", error);
      alert("Error clearing chat history");
    }
  };

  const [pdfViewerState, setPdfViewerState] = useState({
    isOpen: false,
    url: null,
    pageNumber: null,
  });

  // Update the openPdfPage function
  const openPdfPage = async (url, pageNumber) => {
    try {
      const { data: signedUrlData, error } = await supabase.storage
        .from("pdfs")
        .createSignedUrl(url.split("/").pop(), 3600);

      if (error) throw error;

      if (signedUrlData?.signedUrl) {
        // Ensure pageNumber is a valid number
        const page = parseInt(pageNumber, 10) || 1;

        // Log for debugging
        console.log("Opening PDF:", {
          url: signedUrlData.signedUrl,
          pageNumber: page,
        });

        const viewerUrl = `/pdfjs/viewer.html?file=${encodeURIComponent(
          signedUrlData.signedUrl
        )}&page=${page}`;
        window.open(viewerUrl, "_blank");
      }
    } catch (error) {
      console.error("Error opening PDF:", error);
      alert("Error opening PDF");
    }
  };

  // Add close function
  const closePdfViewer = () => {
    setPdfViewerState({
      isOpen: false,
      url: null,
      pageNumber: null,
    });
  };

  // Add this function to fetch chat history
  const fetchChats = async () => {
    try {
      const { data, error } = await supabase
        .from("chats")
        .select("*")
        .order("created_at", { ascending: false });

      if (error) throw error;
      setChats(data || []);
    } catch (error) {
      console.error("Error fetching chats:", error);
    }
  };

  // const handleDocumentSelect = async (doc) => {
  //   setSelectedDoc(doc);
  //   await fetchChatHistory(doc.id);
  // };

  const handleDelete = async (doc) => {
    try {
      // Delete from storage first
      const filename = doc.file_url.split("/").pop();
      const { error: storageError } = await supabase.storage
        .from("pdfs")
        .remove([filename]);

      if (storageError) throw storageError;

      // Delete document (will cascade delete chunks)
      const { error: deleteError } = await supabase
        .from("documents")
        .delete()
        .eq("id", doc.id);

      if (deleteError) throw deleteError;

      // Refresh documents list
      fetchDocuments();
    } catch (error) {
      console.error("Delete error:", error);
      alert("Error deleting document");
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    const { data } = await supabase.from("documents").select("*");
    setDocuments(data || []);
  };

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files).filter((file) => {
      const isValidType = file.type === "application/pdf";
      const isValidSize = file.size <= 10 * 1024 * 1024; // 10MB

      if (!isValidType) {
        alert(`${file.name} is not a PDF file`);
      }
      if (!isValidSize) {
        alert(`${file.name} exceeds 10MB size limit`);
      }

      return isValidType && isValidSize;
    });

    setSelectedFiles(files);
  };

  // In App.js, modify the fetch call in handleUpload:
  // handleUpload in App.js
  const handleUpload = async () => {
    if (!selectedFiles.length) return;

    setLoading(true);
    try {
      for (const file of selectedFiles) {
        setUploadProgress((prev) => ({ ...prev, [file.name]: 0 }));

        // Upload to storage
        const { data: uploadData, error: uploadError } = await supabase.storage
          .from("pdfs")
          .upload(`${Date.now()}_${file.name}`, file);

        if (uploadError) throw uploadError;

        // Get public URL
        const {
          data: { publicUrl },
        } = supabase.storage.from("pdfs").getPublicUrl(uploadData.path);

        // Process PDF content
        const arrayBuffer = await file.arrayBuffer();
        const pdf = await getDocument({ data: arrayBuffer }).promise;
        const totalPages = pdf.numPages;

        // Create document record with total pages
        const { data: docData, error: docError } = await supabase
          .from("documents")
          .insert({
            filename: file.name,
            file_url: publicUrl,
            total_pages: totalPages,
          })
          .select()
          .single();

        if (docError) throw docError;

        let fullText = [];
        for (let i = 1; i <= totalPages; i++) {
          const page = await pdf.getPage(i);
          const textContent = await page.getTextContent();
          const pageText = textContent.items.map((item) => item.str).join(" ");

          fullText.push({
            text: pageText,
            pageNumber: i,
          });

          setUploadProgress((prev) => ({
            ...prev,
            [file.name]: (i / totalPages) * 100,
          }));
        }

        // Process with edge function
        const response = await fetch(
          `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-pdf`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`,
            },
            body: JSON.stringify({
              documentId: docData.id,
              pages: fullText,
            }),
          }
        );

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Failed to process ${file.name}: ${errorText}`);
        }
      }

      setSelectedFiles([]);
      setUploadProgress({});
      fetchDocuments();
    } catch (error) {
      console.error("Upload error:", error);
      alert("Error uploading files: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Modify handleQuerySubmit to store chat history
  // Update handleQuerySubmit to handle multiple documents
{/* Update handleQuerySubmit to remove the event parameter */}
const handleQuerySubmit = async () => {
  if (!query.trim() || selectedDocs.length === 0) return;

  setLoading(true);
  try {
    const documentIds = selectedDocs.map(doc => doc.id);

    // If no chat is selected, create a new one
    let chatId = selectedChat?.id;
    if (!chatId) {
      const { data: newChat, error: chatError } = await supabase
        .from("chats")
        .insert({
          title: query.trim().substring(0, 50), // Use first query as title
          first_query: query.trim(),
          document_ids: documentIds
        })
        .select()
        .single();

      if (chatError) throw chatError;
      chatId = newChat.id;
      setSelectedChat(newChat);
    }

    const response = await fetch(
      `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-query`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`,
        },
        body: JSON.stringify({
          query: query.trim(),
          documentIds: documentIds,
        }),
      }
    );

    if (!response.ok) throw new Error("Failed to process query");

    const result = await response.json();

    // Store message in chat_messages
    const { error: messageError } = await supabase
      .from("chat_messages")
      .insert({
        chat_id: chatId,
        query: query.trim(),
        response: result.answer,
        context: result.context
      });

    if (messageError) throw messageError;

    // Refresh chat history
    await fetchChatHistory(chatId);
    setQuery("");
  } catch (error) {
    console.error("Query error:", error);
    alert("Error processing query: " + error.message);
  } finally {
    setLoading(false);
  }
};

return (
  <div className="container-fluid vh-100">
    <div className="row h-100">
      {/* Left Sidebar - Document Management */}
      <div className="col-md-3 col-lg-2 p-0 border-end bg-white sidebar">
        <div className="sticky-top">
          <div className="p-4 border-bottom">
            <h5 className="mb-3">PDF Documents</h5>
            <div className="d-flex gap-2 mb-3">
              <div className="upload-wrapper">
                <input
                  type="file"
                  accept=".pdf"
                  multiple
                  onChange={handleFileChange}
                  className="d-none"
                  id="fileInput"
                  disabled={loading}
                />
                <label
                  htmlFor="fileInput"
                  className="btn btn-outline-primary d-flex align-items-center gap-2"
                >
                  <FaUpload />
                  Choose Files
                </label>
              </div>
              <button
                className="btn btn-primary"
                onClick={handleUpload}
                disabled={!selectedFiles.length || loading}
              >
                Upload {selectedFiles.length ? `(${selectedFiles.length})` : ""}
              </button>
            </div>

            {/* Selected Files List */}
            {selectedFiles.length > 0 && (
              <div className="selected-files mb-3">
                {Array.from(selectedFiles).map((file, index) => (
                  <div
                    key={index}
                    className="selected-file d-flex justify-content-between align-items-center p-2 bg-light rounded mb-2"
                  >
                    <div className="d-flex align-items-center">
                      <FaFilePdf className="text-primary me-2" />
                      <span className="text-truncate" style={{ maxWidth: "150px" }}>
                        {file.name}
                      </span>
                    </div>
                    <button
                      className="btn btn-link text-danger p-1"
                      onClick={() => {
                        const newFiles = Array.from(selectedFiles).filter(
                          (_, i) => i !== index
                        );
                        setSelectedFiles(newFiles);
                      }}
                    >
                      <FaTrash size={14} />
                    </button>
                  </div>
                ))}
              </div>
            )}

            {/* Upload Progress */}
            {Object.entries(uploadProgress).map(([filename, progress]) => (
              <div key={filename} className="upload-progress-wrapper mb-2">
                <div className="small text-muted mb-1">{filename}</div>
                <div className="progress" style={{ height: "4px" }}>
                  <div
                    className="progress-bar"
                    role="progressbar"
                    style={{ width: `${progress}%` }}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Documents List */}
        <div className="documents-list p-3">
          {documents.map((doc) => (
            <div
              key={doc.id}
              className={`document-item d-flex align-items-center p-2 rounded mb-2 border ${
                selectedDocs.some(d => d.id === doc.id) ? "selected" : ""
              }`}
            >
              <div
                className="d-flex align-items-center flex-grow-1 gap-2 cursor-pointer"
                onClick={() => handleDocumentSelect(doc)}
              >
                <input
                  type="checkbox"
                  checked={selectedDocs.some(d => d.id === doc.id)}
                  onChange={() => handleDocumentSelect(doc)}
                  className="me-2"
                />
                <FaFilePdf className="text-danger" />
                <span className="text-truncate">{doc.filename}</span>
              </div>
              <button
                className="btn btn-link text-danger p-1 delete-btn"
                onClick={() => handleDelete(doc)}
              >
                <FaTrash />
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Middle Sidebar - Chat History */}
      <div className="col-md-3 col-lg-2 p-0 border-end bg-white sidebar">
        <div className="p-4 border-bottom">
          <h5 className="mb-3">Chat History</h5>
          <button 
            className="btn btn-outline-primary w-100"
            onClick={() => setSelectedChat(null)}
          >
            New Chat
          </button>
        </div>
        <div className="chat-history-list">
          {chats.map((chat) => (
            <div
              key={chat.id}
              className={`chat-item p-3 border-bottom cursor-pointer ${
                selectedChat?.id === chat.id ? "selected" : ""
              }`}
              onClick={() => setSelectedChat(chat)}
            >
              <div className="d-flex align-items-center gap-2">
                <FaComments className="text-primary" />
                <div className="chat-preview">
                  <div className="chat-title text-truncate">
                    {chat.title || chat.first_query}
                  </div>
                  <small className="text-muted">
                    {new Date(chat.created_at).toLocaleDateString()}
                  </small>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="col-md-6 col-lg-8 p-0 d-flex flex-column h-100">
        {/* Chat Header */}
        <div className="chat-header p-4 bg-light border-bottom">
          <div className="d-flex justify-content-between align-items-center">
            {selectedChat ? (
              <>
                <h6 className="mb-0">
                  {selectedChat.title || selectedChat.first_query}
                  <br />
                  <small className="text-muted">
                    {selectedDocs.map(doc => doc.filename).join(", ")}
                  </small>
                </h6>
                {chatHistory.length > 0 && (
                  <button
                    className="btn btn-outline-secondary btn-sm d-flex align-items-center gap-2"
                    onClick={clearChat}
                  >
                    <FaTrash /> Clear Chat
                  </button>
                )}
              </>
            ) : (
              <h5 className="mb-0">New Chat</h5>
            )}
          </div>
        </div>

        {/* Chat Messages */}
        <div className="chat-messages flex-grow-1 p-4 overflow-auto">
          {isHistoryLoading ? (
            <div className="text-center text-muted">
              Loading chat history...
            </div>
          ) : (
            <div className="d-flex flex-column gap-4">
              {chatHistory.map((chat, index) => (
                <div
                  key={index}
                  className="d-flex flex-column gap-3"
                  ref={index === chatHistory.length - 1 ? latestMessageRef : null}
                >
                  <div className="message user-message align-self-end">
                    <div className="message-content p-3 bg-primary text-white rounded-3">
                      {chat.query}
                    </div>
                  </div>
                  {renderChatMessage(chat)}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Chat Input */}
        <div className="chat-input p-4 border-top">
          <form 
            onSubmit={(e) => {
              e.preventDefault();
              handleQuerySubmit();
            }} 
            className="d-flex gap-3"
          >
            <input
              type="text"
              className="form-control"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder={
                selectedDocs.length > 0
                  ? "Ask a question about the selected documents..."
                  : "Select at least one document first"
              }
              disabled={selectedDocs.length === 0 || loading}
            />
            <button
              type="submit"
              className="btn btn-primary px-4"
              disabled={selectedDocs.length === 0 || loading || !query.trim()}
            >
              Send
            </button>
          </form>
        </div>
      </div>
    </div>
  </div>
);
}

export default App;
