// frontend/src/App.js
import React, { useState, useEffect, useRef } from 'react';
import { createClient } from '@supabase/supabase-js';
import './App.css';
import PDFViewer from './components/PDFViewer';
// At the top of App.js, add this import:
import { FaUpload } from 'react-icons/fa';
import { FaFilePdf } from 'react-icons/fa';
import { FaTrash } from 'react-icons/fa';

import * as pdfjsLib from 'pdfjs-dist';
import { getDocument } from 'pdfjs-dist';
pdfjsLib.GlobalWorkerOptions.workerSrc = "https://cdn.jsdelivr.net/npm/pdfjs-dist@4.9.155/build/pdf.worker.min.mjs"; 


const supabase = createClient(
  process.env.REACT_APP_SUPABASE_URL,
  process.env.REACT_APP_SUPABASE_ANON_KEY
);



function App() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [selectedDoc, setSelectedDoc] = useState(null);
  const [query, setQuery] = useState('');
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState({});
  const [isHistoryLoading, setIsHistoryLoading] = useState(false);
  const latestMessageRef = useRef(null);
  useEffect(() => {
    if (latestMessageRef.current) {
      latestMessageRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  }, [chatHistory]);

  useEffect(() => {
    if (selectedDoc) {
      fetchChatHistory(selectedDoc.id);
    }
  }, [selectedDoc]);

  const truncateContext = (text, maxLength = 200) => {
    if (!text) return '';

    // If text is shorter than max length, return as is
    if (text.length <= maxLength) return text;

    // Find the last complete sentence within the limit
    const truncated = text.substring(0, maxLength);
    const lastPeriod = truncated.lastIndexOf('.');
    const lastQuestion = truncated.lastIndexOf('?');
    const lastExclamation = truncated.lastIndexOf('!');

    // Find the last sentence end
    const lastSentenceEnd = Math.max(lastPeriod, lastQuestion, lastExclamation);

    if (lastSentenceEnd > 0) {
      // Return up to the last sentence end
      return text.substring(0, lastSentenceEnd + 1) + '...';
    }

    // If no sentence end found, truncate at word boundary
    const lastSpace = truncated.lastIndexOf(' ');
    return text.substring(0, lastSpace) + '...';
  };

  // Add these two new helper functions here:
  const formatAIResponse = (response) => {
    // Split by double newlines to separate sections
    const sections = response.split('\n\n');

    const formattedSections = sections.map((section, index) => {
      // Check if section is a bullet point list
      if (section.includes('• ') || section.includes('* ')) {
        const listItems = section.split(/[•*]\s/).filter(item => item.trim());
        return (
          `<div class="section">
            <ul>
              ${listItems.map(item => `<li>${formatText(item.trim())}</li>`).join('')}
            </ul>
          </div>`
        );
      }

      // Format normal paragraphs
      return `<div class="section"><p>${formatText(section)}</p></div>`;
    });

    return formattedSections.join('');
  };

  const formatText = (text) => {
    // First handle bold text (text between pairs of asterisks)
    let formattedText = text.replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>');

    // Then format numbers
    formattedText = formattedText.replace(
      /(\$?\d+(?:,\d{3})*(?:\.\d+)?(?:\s*(?:million|billion|trillion))?)/g,
      '<span class="numeric-value">$1</span>'
    );

    return formattedText;
  };



  const clearChat = async () => {
    if (!selectedDoc) return;

    try {
      // Delete from database
      const { error } = await supabase
        .from('chat_history')
        .delete()
        .eq('document_id', selectedDoc.id);

      if (error) throw error;

      // Clear local state
      setChatHistory([]);
    } catch (error) {
      console.error('Error clearing chat:', error);
      alert('Error clearing chat history');
    }
  };


  const [pdfViewerState, setPdfViewerState] = useState({
    isOpen: false,
    url: null,
    pageNumber: null
  });

  // Update the openPdfPage function
  const openPdfPage = async (url, pageNumber) => {
    try {
      const { data: signedUrlData, error } = await supabase
        .storage
        .from('pdfs')
        .createSignedUrl(url.split('/').pop(), 3600);

      if (error) throw error;

      if (signedUrlData?.signedUrl) {
        // Ensure pageNumber is a valid number
        const page = parseInt(pageNumber, 10) || 1;

        // Log for debugging
        console.log('Opening PDF:', {
          url: signedUrlData.signedUrl,
          pageNumber: page
        });

        const viewerUrl = `/pdfjs/viewer.html?file=${encodeURIComponent(signedUrlData.signedUrl)}&page=${page}`;
        window.open(viewerUrl, '_blank');
      }
    } catch (error) {
      console.error('Error opening PDF:', error);
      alert('Error opening PDF');
    }
  };


  // Add close function
  const closePdfViewer = () => {
    setPdfViewerState({
      isOpen: false,
      url: null,
      pageNumber: null
    });
  };

  // Add this function to fetch chat history
  const fetchChatHistory = async (documentId) => {
  setIsHistoryLoading(true);
  try {
    const { data, error } = await supabase
      .from('chat_history')
      .select('*')
      .eq('document_id', documentId)
      .order('created_at', { ascending: true });

    if (error) throw error;
    setChatHistory(data || []);
  } catch (error) {
    console.error('Error fetching chat history:', error);
  } finally {
    setIsHistoryLoading(false);
  }
};

  const handleDocumentSelect = async (doc) => {
  setSelectedDoc(doc);
  await fetchChatHistory(doc.id);
};


const handleDelete = async (doc) => {
  try {
    // Delete from storage first
    const filename = doc.file_url.split('/').pop();
    const { error: storageError } = await supabase.storage
      .from('pdfs')
      .remove([filename]);

    if (storageError) throw storageError;

    // Delete document (will cascade delete chunks)
    const { error: deleteError } = await supabase
      .from('documents')
      .delete()
      .eq('id', doc.id);

    if (deleteError) throw deleteError;

    // Refresh documents list
    fetchDocuments();
  } catch (error) {
    console.error('Delete error:', error);
    alert('Error deleting document');
  }
};


  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    const { data } = await supabase.from('documents').select('*');
    setDocuments(data || []);
  };

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files).filter(file => {
      const isValidType = file.type === 'application/pdf';
      const isValidSize = file.size <= 10 * 1024 * 1024; // 10MB

      if (!isValidType) {
        alert(`${file.name} is not a PDF file`);
      }
      if (!isValidSize) {
        alert(`${file.name} exceeds 10MB size limit`);
      }

      return isValidType && isValidSize;
    });

    setSelectedFiles(files);
  };

  // In App.js, modify the fetch call in handleUpload:
  // handleUpload in App.js
const handleUpload = async () => {
  if (!selectedFiles.length) return;

  setLoading(true);
  try {
    for (const file of selectedFiles) {
      setUploadProgress(prev => ({ ...prev, [file.name]: 0 }));

      // Upload to storage
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('pdfs')
        .upload(`${Date.now()}_${file.name}`, file);

      if (uploadError) throw uploadError;

      // Get public URL
      const { data: { publicUrl } } = supabase.storage
        .from('pdfs')
        .getPublicUrl(uploadData.path);

      // Process PDF content
      const arrayBuffer = await file.arrayBuffer();
      const pdf = await getDocument({ data: arrayBuffer }).promise;
      const totalPages = pdf.numPages;

      // Create document record with total pages
      const { data: docData, error: docError } = await supabase
        .from('documents')
        .insert({
          filename: file.name,
          file_url: publicUrl,
          total_pages: totalPages
        })
        .select()
        .single();

      if (docError) throw docError;

      let fullText = [];
      for (let i = 1; i <= totalPages; i++) {
        const page = await pdf.getPage(i);
        const textContent = await page.getTextContent();
        const pageText = textContent.items.map(item => item.str).join(' ');

        fullText.push({
          text: pageText,
          pageNumber: i
        });

        setUploadProgress(prev => ({
          ...prev,
          [file.name]: (i / totalPages) * 100
        }));
      }

      // Process with edge function
      const response = await fetch(
        `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-pdf`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`
          },
          body: JSON.stringify({
            documentId: docData.id,
            pages: fullText
          })
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`Failed to process ${file.name}: ${errorText}`);
      }
    }

    setSelectedFiles([]);
    setUploadProgress({});
    fetchDocuments();
  } catch (error) {
    console.error('Upload error:', error);
    alert('Error uploading files: ' + error.message);
  } finally {
    setLoading(false);
  }
};





  // Modify handleQuerySubmit to store chat history
  const handleQuerySubmit = async (e) => {
    e.preventDefault();
    if (!query.trim() || !selectedDoc) return;

    setLoading(true);
    try {
      // First get the document details to ensure we have the correct ID
      const { data: documentData, error: documentError } = await supabase
        .from('documents')
        .select('id')
        .eq('id', selectedDoc.id)
        .single();

      if (documentError) throw documentError;

      const response = await fetch(
        `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-query`,
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`
          },
          body: JSON.stringify({
            query: query.trim(),
            documentId: documentData.id, // Use the ID from the database
            chatHistory: chatHistory
          })
        }
      );

      if (!response.ok) {
        const errorText = await response.text();
        throw new Error('Failed to process query: ' + errorText);
      }

      const result = await response.json();

      // Create new chat entry
      const newEntry = {
        document_id: documentData.id,
        query: query.trim(),
        response: result.answer,
        context: result.context
      };

      // Store in database
      const { error: insertError } = await supabase
        .from('chat_history')
        .insert(newEntry);

      if (insertError) throw insertError;

      // Update local state
      await fetchChatHistory(documentData.id);
      setQuery('');
    } catch (error) {
      console.error('Query error:', error);
      alert('Error processing query: ' + error.message);
    } finally {
      setLoading(false);
    }
  };



return (
  <div className="container-fluid vh-100">
    <div className="row h-100">
      {/* Sidebar */}
      <div className="col-md-3 col-lg-3 p-0 border-end bg-white sidebar">
        <div className="sticky-top">
          <div className="p-4 border-bottom">
            <h5 className="mb-3">PDF Documents</h5>
            <div className="d-flex gap-2 mb-3">
              <div className="upload-wrapper">
                <input
                  type="file"
                  accept=".pdf"
                  multiple
                  onChange={handleFileChange}
                  className="d-none"
                  id="fileInput"
                  disabled={loading}
                />
                <label htmlFor="fileInput" className="btn btn-outline-primary d-flex align-items-center gap-2">
                  <FaUpload />
                  Choose Files
                </label>
              </div>
              <button 
                className="btn btn-primary"
                onClick={handleUpload}
                disabled={!selectedFiles.length || loading}
              >
                Upload {selectedFiles.length ? `(${selectedFiles.length})` : ''}
              </button>
            </div>

            {/* Selected Files List */}
            {selectedFiles.length > 0 && (
              <div className="selected-files mb-3">
                {Array.from(selectedFiles).map((file, index) => (
                  <div key={index} className="selected-file d-flex justify-content-between align-items-center p-2 bg-light rounded mb-2">
                    <div className="d-flex align-items-center">
                      <FaFilePdf className="text-primary me-2" />
                      <span className="text-truncate" style={{ maxWidth: '150px' }}>{file.name}</span>
                    </div>
                    <button 
                      className="btn btn-link text-danger p-1"
                      onClick={() => {
                        const newFiles = Array.from(selectedFiles).filter((_, i) => i !== index);
                        setSelectedFiles(newFiles);
                      }}
                    >
                      <FaTrash size={14} />
                    </button>
                  </div>
                ))}
              </div>
            )}

            {/* Upload Progress */}
            {Object.entries(uploadProgress).map(([filename, progress]) => (
              <div key={filename} className="upload-progress-wrapper mb-2">
                <div className="small text-muted mb-1">{filename}</div>
                <div className="progress" style={{ height: '4px' }}>
                  <div 
                    className="progress-bar" 
                    role="progressbar" 
                    style={{ width: `${progress}%` }}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>



          {/* Documents List */}
          <div className="documents-list p-3">
            {documents.map(doc => (
              <div
                key={doc.id}
                className={`document-item d-flex align-items-center p-2 rounded mb-2 border ${
                  selectedDoc?.id === doc.id ? 'selected' : ''
                }`}
              >
                <div 
                  className="d-flex align-items-center flex-grow-1 gap-2 cursor-pointer"
                  onClick={() => handleDocumentSelect(doc)}
                >
                  <FaFilePdf className="text-danger" />
                  <span className="text-truncate">{doc.filename}</span>
                </div>
                <button 
                  className="btn btn-link text-danger p-1 delete-btn"
                  onClick={() => handleDelete(doc)}
                >
                  <FaTrash />
                </button>
              </div>
            ))}
          </div>
        </div>

        {/* Main Chat Area */}
        <div className="col-md-9 col-lg-9 p-0 d-flex flex-column h-100">
          {/* Chat Header */}
          <div className="chat-header p-4 bg-light border-bottom">
            <div className="d-flex justify-content-between align-items-center">
              {selectedDoc ? (
                <>
                  <h6 className="mb-0">Chat About: <br></br><i><strong>{selectedDoc.filename}</strong></i></h6>
                  {chatHistory.length > 0 && (
                    <button 
                      className="btn btn-outline-secondary btn-sm d-flex align-items-center gap-2"
                      onClick={clearChat}
                    >
                      <FaTrash /> Clear Chat
                    </button>
                  )}
                </>
              ) : (
                <h5 className="mb-0">Select a document to start chatting</h5>
              )}
            </div>
          </div>

          {/* Chat Messages */}
          <div className="chat-messages flex-grow-1 p-4 overflow-auto">
            {isHistoryLoading ? (
              <div className="text-center text-muted">Loading chat history...</div>
            ) : (
              <div className="d-flex flex-column gap-4">
                {chatHistory.map((chat, index) => (
                  <div 
                    key={index} 
                    className="d-flex flex-column gap-3"
                    ref={index === chatHistory.length - 1 ? latestMessageRef : null}
                  >
                    {/* User Message */}
                    <div className="message user-message align-self-end">
                      <div className="message-content p-3 bg-primary text-white rounded-3">
                        {chat.query}
                      </div>
                    </div>

                    {/* AI Message */}
                    <div className="message ai-message align-self-start">
                      <div className="message-content p-3 rounded-3">
                        <div dangerouslySetInnerHTML={{ 
                          __html: formatAIResponse(chat.response) 
                        }} />

                        {chat.context && (
                          <div className="context-info">
                            <small className="text-muted">Relevant Source Excerpts:</small>
                            <div className="context-content">
                              {chat.context.slice(0, 3).map((ctx, idx) => (
                                <div key={idx} className="context-item d-flex justify-content-between align-items-start gap-3 p-2 bg-white rounded mb-2">
                                  <p className="mb-0 small">
                                    {truncateContext(ctx.content)}
                                  </p>
                                  <button
                                    className="btn btn-outline-primary btn-sm flex-shrink-0"
                                    onClick={() => openPdfPage(selectedDoc.file_url, ctx.page_number)}
                                  >
                                    Page {ctx.page_number}
                                  </button>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Chat Input */}
          <div className="chat-input p-4  border-top">
            <form onSubmit={handleQuerySubmit} className="d-flex gap-3">
              <input
                type="text"
                className="form-control"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder={selectedDoc ? "Ask a question..." : "Select a document first"}
                disabled={!selectedDoc || loading}
              />
              <button 
                type="submit" 
                className="btn btn-primary px-4"
                disabled={!selectedDoc || loading || !query.trim()}
              >
                Send
              </button>
            </form>
          </div>
        </div>
      </div>

      {/* PDF Viewer Modal */}
      {pdfViewerState.isOpen && (
        <PDFViewer 
          url={pdfViewerState.url}
          pageNumber={pdfViewerState.pageNumber}
          onClose={closePdfViewer}
        />
      )}
    </div>
  );
};

export default App;
