import React, { useState } from 'react';
import { ChevronDown, ChevronUp } from 'lucide-react@0.263.1';

const MessageFormatter = ({ content, context, selectedDocs, openPdfPage }) => {
  const [isContextExpanded, setIsContextExpanded] = useState(true);

  const truncateContext = (text, maxLength = 200) => {
    if (!text) return "";
    if (text.length <= maxLength) return text;

    const truncated = text.substring(0, maxLength);
    const lastPeriod = truncated.lastIndexOf(".");
    const lastQuestion = truncated.lastIndexOf("?");
    const lastExclamation = truncated.lastIndexOf("!");

    const lastSentenceEnd = Math.max(lastPeriod, lastQuestion, lastExclamation);

    if (lastSentenceEnd > 0) {
      return text.substring(0, lastSentenceEnd + 1) + "...";
    }

    const lastSpace = truncated.lastIndexOf(" ");
    return text.substring(0, lastSpace) + "...";
  };

  const formatText = (text) => {
    if (!text) return '';

    let formattedText = text
      .replace(/^(\*{3})/g, '')
      .replace(/(\*\*|__)(.*?)\1/g, '<strong>$2</strong>')
      .replace(/(\*|_)(.*?)\1/g, '<em>$2</em>')
      .replace(/^(#{1,6})\s+(.+)$/gm, (match, hashes, content) => {
        const level = hashes.length;
        return `<h${level} class="text-${getHeaderSize(level)} font-bold my-2">${content}</h${level}>`;
      })
      .replace(/(\$?\d+(?:,\d{3})*(?:\.\d+)?(?:\s*(?:million|billion|trillion))?)/g, 
        '<span class="font-mono">$1</span>');

    return formattedText;
  };

  const getHeaderSize = (level) => {
    const sizes = {
      1: 'xl',
      2: 'lg',
      3: 'base',
      4: 'sm',
      5: 'xs',
      6: 'xs'
    };
    return sizes[level] || 'base';
  };

  const formatSection = (section) => {
    const lines = section.split('\n');
    const formattedLines = [];
    let currentList = [];

    lines.forEach((line, index) => {
      const trimmedLine = line.trim();

      if (trimmedLine.startsWith('***')) {
        const bulletContent = trimmedLine.substring(3).trim();
        currentList.push(bulletContent);
      } 
      else if (trimmedLine.startsWith('**')) {
        if (currentList.length > 0) {
          formattedLines.push(
            <ul key={`list-${index}`} className="list-disc pl-6 space-y-2">
              {currentList.map((item, idx) => (
                <li 
                  key={idx} 
                  className="mb-2"
                  dangerouslySetInnerHTML={{ __html: formatText(item) }}
                />
              ))}
            </ul>
          );
          currentList = [];
        }

        formattedLines.push(
          <p 
            key={`para-${index}`}
            className="mb-4"
            dangerouslySetInnerHTML={{ __html: formatText(trimmedLine) }}
          />
        );
      }
      else {
        if (currentList.length > 0) {
          formattedLines.push(
            <ul key={`list-${index}`} className="list-disc pl-6 space-y-2">
              {currentList.map((item, idx) => (
                <li 
                  key={idx} 
                  className="mb-2"
                  dangerouslySetInnerHTML={{ __html: formatText(item) }}
                />
              ))}
            </ul>
          );
          currentList = [];
        }

        if (trimmedLine) {
          formattedLines.push(
            <p 
              key={`para-${index}`}
              className="mb-4"
              dangerouslySetInnerHTML={{ __html: formatText(trimmedLine) }}
            />
          );
        }
      }
    });

    if (currentList.length > 0) {
      formattedLines.push(
        <ul key="final-list" className="list-disc pl-6 space-y-2">
          {currentList.map((item, idx) => (
            <li 
              key={idx} 
              className="mb-2"
              dangerouslySetInnerHTML={{ __html: formatText(item) }}
            />
          ))}
        </ul>
      );
    }

    return <>{formattedLines}</>;
  };

  const sections = content
    .split('\n\n')
    .filter(section => section.trim());

  return (
    <div className="message-content">
      {sections.map((section, idx) => (
        <div key={idx} className="mb-4">
          {formatSection(section)}
        </div>
      ))}

      {context && context.length > 0 && (
        <div className="context-info mt-6 bg-gray-50 p-4 rounded-lg">
          <div 
            className="flex items-center justify-between cursor-pointer"
            onClick={() => setIsContextExpanded(!isContextExpanded)}
          >
            <h4 className="text-sm font-semibold text-gray-600 mb-0">Relevant Source Excerpts:</h4>
            <button className="text-gray-500 hover:text-gray-700">
              {isContextExpanded ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
            </button>
          </div>

          {isContextExpanded && (
            <div className="space-y-3 mt-3">
              {context.map((ctx, idx) => {
                const relatedDoc = selectedDocs.find(d => d.id === ctx.documentId);
                return (
                  <div key={idx} className="bg-white p-3 rounded-md shadow-sm">
                    <div className="flex justify-between items-start gap-4">
                      <div className="flex-1">
                        <p className="text-sm text-gray-500 mb-1">
                          From: {relatedDoc?.filename || `Document ${ctx.documentId}`}
                        </p>
                        <p className="text-sm">{truncateContext(ctx.content)}</p>
                      </div>
                      <button
                        className="px-3 py-1 text-sm text-blue-600 hover:bg-blue-50 rounded"
                        onClick={() => openPdfPage(relatedDoc?.file_url, ctx.page_number)}
                      >
                        Page {ctx.page_number}
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default MessageFormatter;
