// frontend/src/App.js
import React, { useState, useEffect, useRef } from "react";
import { createClient } from "@supabase/supabase-js";
import "./App.css";
import PDFViewer from "./components/PDFViewer";

import { FaUpload, FaFilePdf, FaTrash, FaPlus, FaComments  } from "react-icons/fa";

import * as pdfjsLib from "pdfjs-dist";
import { getDocument } from "pdfjs-dist";
import MessageFormatter from './components/MessageFormatter';
import ChatHistory from "./components/ChatHistory";
pdfjsLib.GlobalWorkerOptions.workerSrc =
  "https://cdn.jsdelivr.net/npm/pdfjs-dist@4.9.155/build/pdf.worker.min.mjs";

const supabase = createClient(
  process.env.REACT_APP_SUPABASE_URL,
  process.env.REACT_APP_SUPABASE_ANON_KEY
);

function App() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [documents, setDocuments] = useState([]);
  const [selectedDoc, setSelectedDoc] = useState(null);
  const [selectedDocs, setSelectedDocs] = useState([]);
  const [query, setQuery] = useState("");
  const [chatHistory, setChatHistory] = useState([]);
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState({});
  const [isHistoryLoading, setIsHistoryLoading] = useState(false);
  const latestMessageRef = useRef(null);
  const [chats, setChats] = useState([]);
  const [selectedChat, setSelectedChat] = useState(null);
  const [isCreatingNewChat, setIsCreatingNewChat] = useState(false);
  const [currentChatId, setCurrentChatId] = useState(null);

  const renderChatMessage = (chat) => (
    <div className="message ai-message align-self-start">
      <div className="message-content p-3 rounded-3">
        <MessageFormatter 
          content={chat.response}
          context={chat.context}
          selectedDocs={selectedDocs}
          openPdfPage={openPdfPage}
        />
      </div>
    </div>
  );

  useEffect(() => {
    // Fetch both documents and chats when component mounts
    const fetchInitialData = async () => {
      await Promise.all([
        fetchDocuments(),
        fetchChats()
      ]);
    };

    fetchInitialData();
  }, []); // Empty dependency array means this runs once on mount

  useEffect(() => {
    if (latestMessageRef.current) {
      latestMessageRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [chatHistory]);

  useEffect(() => {
    if (selectedDoc) {
      fetchChatHistory(selectedDoc.id);
    }
  }, [selectedDoc]);

  // Add this function to handle multiple document selection
const handleDocumentSelect = (doc) => {
  setSelectedDocs(prev => {
    const isSelected = prev.some(d => d.id === doc.id);
    if (isSelected) {
      return prev.filter(d => d.id !== doc.id);
    } else {
      return [...prev, doc];
    }
  });
};


  // Add these two new helper functions here:


  const clearChat = async () => {
  if (!selectedChat) return;

  try {
    // Delete from database
    const { error } = await supabase
      .from("chats")
      .delete()
      .eq("id", selectedChat.id);

    if (error) throw error;

    // Clear local state
    setChatHistory([]);
    setSelectedChat(null);
    await fetchChats(); // Refresh the chat list

  } catch (error) {
    console.error("Error clearing chat:", error);
    alert("Error clearing chat history");
  }
};

  const [pdfViewerState, setPdfViewerState] = useState({
    isOpen: false,
    url: null,
    pageNumber: null,
  });

  // Update the openPdfPage function
  const openPdfPage = async (url, pageNumber) => {
    try {
      const { data: signedUrlData, error } = await supabase.storage
        .from("pdfs")
        .createSignedUrl(url.split("/").pop(), 3600);

      if (error) throw error;

      if (signedUrlData?.signedUrl) {
        // Ensure pageNumber is a valid number
        const page = parseInt(pageNumber, 10) || 1;

        // Log for debugging
        console.log("Opening PDF:", {
          url: signedUrlData.signedUrl,
          pageNumber: page,
        });

        const viewerUrl = `/pdfjs/viewer.html?file=${encodeURIComponent(
          signedUrlData.signedUrl
        )}&page=${page}`;
        window.open(viewerUrl, "_blank");
      }
    } catch (error) {
      console.error("Error opening PDF:", error);
      alert("Error opening PDF");
    }
  };

  // Add close function
  const closePdfViewer = () => {
    setPdfViewerState({
      isOpen: false,
      url: null,
      pageNumber: null,
    });
  };

  // Add this function to fetch chat history
  const fetchChats = async () => {
    try {
      const { data, error } = await supabase
        .from('chats')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      setChats(data || []);
    } catch (error) {
      console.error('Error fetching chats:', error);
    }
  };

  const fetchChatMessages = async (chatId) => {
    if (!chatId) {
      console.log('No chat ID provided to fetchChatMessages');
      return;
    }

    setIsHistoryLoading(true);
    try {
      const { data, error } = await supabase
        .from("chat_messages")
        .select("*")
        .eq("chat_id", chatId)
        .order("created_at", { ascending: true });

      if (error) throw error;

      // Ensure we're setting a valid array even if data is null
      setChatHistory(data || []);
    } catch (error) {
      console.error("Error fetching chat messages:", error);
      setChatHistory([]); // Reset to empty array on error
    } finally {
      setIsHistoryLoading(false);
    }
  };

  const getDocumentIds = (docs) => {
    if (!Array.isArray(docs) || !docs.length) return [];
    return docs
      .filter(doc => doc && doc.id)
      .map(doc => typeof doc.id === 'string' ? parseInt(doc.id, 10) : doc.id)
      .filter(id => !isNaN(id));
  };

  // const handleDocumentSelect = async (doc) => {
  //   setSelectedDoc(doc);
  //   await fetchChatHistory(doc.id);
  // };

  const handleDelete = async (doc) => {
    try {
      // Delete from storage first
      const filename = doc.file_url.split("/").pop();
      const { error: storageError } = await supabase.storage
        .from("pdfs")
        .remove([filename]);

      if (storageError) throw storageError;

      // Delete document (will cascade delete chunks)
      const { error: deleteError } = await supabase
        .from("documents")
        .delete()
        .eq("id", doc.id);

      if (deleteError) throw deleteError;

      // Refresh documents list
      fetchDocuments();
    } catch (error) {
      console.error("Delete error:", error);
      alert("Error deleting document");
    }
  };

  useEffect(() => {
    fetchDocuments();
  }, []);

  const fetchDocuments = async () => {
    try {
      const { data, error } = await supabase
        .from('documents')
        .select('*')
        .order('created_at', { ascending: false });

      if (error) throw error;
      console.log('Fetched documents:', data); // For debugging
      setDocuments(data || []);
    } catch (error) {
      console.error('Error fetching documents:', error);
    }
  };

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files).filter((file) => {
      const isValidType = file.type === "application/pdf";
      const isValidSize = file.size <= 10 * 1024 * 1024; // 10MB

      if (!isValidType) {
        alert(`${file.name} is not a PDF file`);
      }
      if (!isValidSize) {
        alert(`${file.name} exceeds 10MB size limit`);
      }

      return isValidType && isValidSize;
    });

    setSelectedFiles(files);
  };

  // In App.js, modify the fetch call in handleUpload:
  // handleUpload in App.js
  const handleUpload = async () => {
    if (!selectedFiles.length) return;

    setLoading(true);
    try {
      for (const file of selectedFiles) {
        setUploadProgress((prev) => ({ ...prev, [file.name]: 0 }));

        // Upload to storage
        const { data: uploadData, error: uploadError } = await supabase.storage
          .from("pdfs")
          .upload(`${Date.now()}_${file.name}`, file);

        if (uploadError) throw uploadError;

        // Get public URL
        const {
          data: { publicUrl },
        } = supabase.storage.from("pdfs").getPublicUrl(uploadData.path);

        // Process PDF content
        const arrayBuffer = await file.arrayBuffer();
        const pdf = await getDocument({ data: arrayBuffer }).promise;
        const totalPages = pdf.numPages;

        // Create document record with total pages
        const { data: docData, error: docError } = await supabase
          .from("documents")
          .insert({
            filename: file.name,
            file_url: publicUrl,
            total_pages: totalPages,
          })
          .select()
          .single();

        if (docError) throw docError;

        let fullText = [];
        for (let i = 1; i <= totalPages; i++) {
          const page = await pdf.getPage(i);
          const textContent = await page.getTextContent();
          const pageText = textContent.items.map((item) => item.str).join(" ");

          fullText.push({
            text: pageText,
            pageNumber: i,
          });

          setUploadProgress((prev) => ({
            ...prev,
            [file.name]: (i / totalPages) * 100,
          }));
        }

        // Process with edge function
        const response = await fetch(
          `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-pdf`,
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Authorization: `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`,
            },
            body: JSON.stringify({
              documentId: docData.id,
              pages: fullText,
            }),
          }
        );

        if (!response.ok) {
          const errorText = await response.text();
          throw new Error(`Failed to process ${file.name}: ${errorText}`);
        }
      }

      setSelectedFiles([]);
      setUploadProgress({});
      fetchDocuments();
    } catch (error) {
      console.error("Upload error:", error);
      alert("Error uploading files: " + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleChatSelect = async (chat) => {
    setSelectedChat(chat);
    setSelectedDocs(chat.document_ids.map(id => 
      documents.find(doc => doc.id === id)
    ).filter(Boolean));
    await fetchChatHistory(chat.id);
  };

  // Update the New Chat button handler
  // Modify handleNewChat
  const handleNewChat = async () => {
    try {
      // Ensure selectedDocs exists and has elements before mapping
      const documentIds = selectedDocs?.length ? selectedDocs.map(doc => doc.id) : [];

      // Create a new chat entry
      const { data: newChat, error: chatError } = await supabase
        .from('chats')
        .insert({
          title: 'New Chat',
          document_ids: documentIds
        })
        .select()
        .single();

      if (chatError) throw chatError;

      // Store the initialization message
      const { error: messageError } = await supabase
        .from('chat_messages')
        .insert({
          chat_id: newChat.id,
          query: 'Chat Started',
          response: 'New chat initialized',
          type: 'system',
          document_ids: documentIds
        });

      if (messageError) throw messageError;

      setCurrentChatId(newChat.id);
      setChatHistory([]);

      // Fetch the new chat's messages
      await fetchChatMessages(newChat.id);
    } catch (error) {
      console.error('Error creating new chat:', error);
      alert('Error creating new chat: ' + error.message);
    }
  };

  // Modify handleQuerySubmit to store chat history
  // Update handleQuerySubmit to handle multiple documents
{/* Update handleQuerySubmit to remove the event parameter */}
const handleQuerySubmit = async () => {
  if (!query?.trim() || !selectedDocs?.length) {
    alert('Please enter a query and select at least one document');
    return;
  }

  // If no current chat, create one
  if (!currentChatId) {
    await handleNewChat();
    return; // Return here to let the new chat creation complete first
  }

  setLoading(true);
  try {
    // Ensure documentIds is properly initialized
    const documentIds = selectedDocs.map(doc => 
      typeof doc.id === 'string' ? parseInt(doc.id, 10) : doc.id
    ).filter(id => !isNaN(id)); // Filter out any invalid IDs

    if (!documentIds.length) {
      throw new Error('No valid document IDs found');
    }

    const response = await fetch(
      `${process.env.REACT_APP_SUPABASE_URL}/functions/v1/process-query`,
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${process.env.REACT_APP_SUPABASE_ANON_KEY}`,
        },
        body: JSON.stringify({
          query: query.trim(),
          documentIds: documentIds
        }),
      }
    );

    if (!response.ok) {
      const errorText = await response.text();
      throw new Error(errorText);
    }

    const result = await response.json();

    // Store both query and response in a single message
    const { error: messageError } = await supabase
      .from("chat_messages")
      .insert({
        chat_id: currentChatId,
        query: query.trim(),
        response: result.answer,
        context: result.context || null,
        document_ids: documentIds,
        type: 'conversation'
      });

    if (messageError) throw messageError;

    // Update chat title if it's the first message
    if (!chatHistory?.length) {
      const { error: updateError } = await supabase
        .from('chats')
        .update({ title: query.trim() })
        .eq('id', currentChatId);

      if (updateError) throw updateError;
    }

    // Fetch updated chat messages
    await fetchChatMessages(currentChatId);
    setQuery("");
  } catch (error) {
    console.error("Query error:", error);
    alert("Error processing query: " + error.message);
  } finally {
    setLoading(false);
  }
};

// Update fetchChatHistory to work with integer IDs
const fetchChatHistory = async (chatId) => {
  setIsHistoryLoading(true);
  try {
    let query = supabase
      .from("chat_history")
      .select("*")
      .order("created_at", { ascending: true });

    // Only add the chat_id filter if it exists
    if (chatId) {
      query = query.eq("chat_id", chatId);
    }

    const { data, error } = await query;

    if (error) throw error;
    setChatHistory(data || []);
  } catch (error) {
    console.error("Error fetching chat history:", error);
  } finally {
    setIsHistoryLoading(false);
  }
};

return (
  <div className="container-fluid vh-100">
    <div className="row h-100">
      {/* Single Sidebar */}
      <div className="col-md-3 col-lg-3 p-0 border-end bg-white sidebar">
        <div className="sticky-top">
          {/* PDF Documents Section */}
          <div className="p-4 border-bottom">
            <h5 className="mb-3">PDF Documents</h5>
            <div className="d-flex gap-2 mb-3">
              <div className="upload-wrapper">
                <input
                  type="file"
                  accept=".pdf"
                  multiple
                  onChange={handleFileChange}
                  className="d-none"
                  id="fileInput"
                  disabled={loading}
                />
                <label
                  htmlFor="fileInput"
                  className="btn btn-outline-primary d-flex align-items-center gap-2"
                >
                  <FaUpload />
                  Choose Files
                </label>
              </div>
              <button
                className="btn btn-primary"
                onClick={handleUpload}
                disabled={!selectedFiles.length || loading}
              >
                Upload {selectedFiles.length ? `(${selectedFiles.length})` : ""}
              </button>
            </div>

            {/* Selected Files List */}
            {selectedFiles.length > 0 && (
              <div className="selected-files mb-3">
                {Array.from(selectedFiles).map((file, index) => (
                  <div
                    key={index}
                    className="selected-file d-flex justify-content-between align-items-center p-2 bg-light rounded mb-2"
                  >
                    <div className="d-flex align-items-center">
                      <FaFilePdf className="text-primary me-2" />
                      <span className="text-truncate" style={{ maxWidth: "150px" }}>
                        {file.name}
                      </span>
                    </div>
                    <button
                      className="btn btn-link text-danger p-1"
                      onClick={() => {
                        const newFiles = Array.from(selectedFiles).filter(
                          (_, i) => i !== index
                        );
                        setSelectedFiles(newFiles);
                      }}
                    >
                      <FaTrash size={14} />
                    </button>
                  </div>
                ))}
              </div>
            )}

            {/* Upload Progress */}
            {Object.entries(uploadProgress).map(([filename, progress]) => (
              <div key={filename} className="upload-progress-wrapper mb-2">
                <div className="small text-muted mb-1">{filename}</div>
                <div className="progress" style={{ height: "4px" }}>
                  <div
                    className="progress-bar"
                    role="progressbar"
                    style={{ width: `${progress}%` }}
                  />
                </div>
              </div>
            ))}
          </div>

          {/* Chat History Section */}
          <div className="p-4">
            <button 
              className="btn btn-primary w-100 mb-4 d-flex align-items-center justify-content-center gap-2"
              onClick={handleNewChat}
            >
              <FaPlus size={14} /> New Chat
            </button>

            <div className="chat-list">
              {Array.from(new Set(chatHistory.map(ch => ch.chat_id))).map(chatId => {
                const firstMessage = chatHistory.find(ch => ch.chat_id === chatId);
                return (
                  <div
                    key={chatId}
                    className={`chat-item p-3 rounded mb-2 cursor-pointer ${
                      currentChatId === chatId ? "selected" : ""
                    }`}
                    onClick={() => {
                      setCurrentChatId(chatId);
                      fetchChatHistory(chatId);
                    }}
                  >
                    <div className="d-flex align-items-center gap-2">
                      <FaComments className="text-primary" />
                      <div className="chat-preview">
                        <div className="chat-title text-truncate">
                          {firstMessage?.query || `Chat ${chatId}`}
                        </div>
                        <small className="text-muted">
                          {new Date(firstMessage?.created_at).toLocaleDateString()}
                        </small>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {/* Documents List */}
          <div className="documents-list p-3">
            {documents.map((doc) => (
              <div
                key={doc.id}
                className={`document-item d-flex align-items-center p-2 rounded mb-2 border ${
                  selectedDocs.some(d => d.id === doc.id) ? "selected" : ""
                }`}
              >
                <div
                  className="d-flex align-items-center flex-grow-1 gap-2 cursor-pointer"
                  onClick={() => handleDocumentSelect(doc)}
                >
                  <input
                    type="checkbox"
                    checked={selectedDocs.some(d => d.id === doc.id)}
                    onChange={() => handleDocumentSelect(doc)}
                    className="me-2"
                  />
                  <FaFilePdf className="text-danger" />
                  <span className="text-truncate">{doc.filename}</span>
                </div>
                <button
                  className="btn btn-link text-danger p-1 delete-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete(doc);
                  }}
                >
                  <FaTrash />
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="col-md-9 col-lg-9 p-0 d-flex flex-column h-100">
        {/* Chat Header */}
        <div className="chat-header p-4 bg-light border-bottom">
          <div className="d-flex justify-content-between align-items-center">
            {selectedDocs.length > 0 ? (
              <>
                <h6 className="mb-0">
                  Selected Documents ({selectedDocs.length}):
                  <br />
                  <small className="text-muted">
                    {selectedDocs.map(doc => doc.filename).join(", ")}
                  </small>
                </h6>
                {chatHistory.length > 0 && (
                  <button
                    className="btn btn-outline-secondary btn-sm d-flex align-items-center gap-2"
                    onClick={clearChat}
                  >
                    <FaTrash /> Clear Chat
                  </button>
                )}
              </>
            ) : (
              <h5 className="mb-0">Select documents to start chatting</h5>
            )}
          </div>
        </div>

        {/* Chat Messages */}
        <div className="chat-messages flex-grow-1 p-4 overflow-auto">
          {isHistoryLoading ? (
            <div className="text-center text-muted">
              Loading chat history...
            </div>
          ) : (
            <div className="d-flex flex-column gap-4">
              {chatHistory.map((chat, index) => (
                <div
                  key={index}
                  className="d-flex flex-column gap-3"
                  ref={index === chatHistory.length - 1 ? latestMessageRef : null}
                >
                  <div className="message user-message align-self-end">
                    <div className="message-content p-3 bg-primary text-white rounded-3">
                      {chat.query}
                    </div>
                  </div>
                  {renderChatMessage(chat)}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Chat Input */}
        <div className="chat-input p-4 border-top">
        <form 
          onSubmit={(e) => {
            e.preventDefault();
            handleQuerySubmit();
          }} 
          className="d-flex gap-3"
        >
          <input
            type="text"
            className="form-control"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder={
              selectedDocs.length > 0
                ? "Ask a question about the selected documents..."
                : "Select at least one document first"
            }
            disabled={selectedDocs.length === 0 || loading}
          />
          <button
            type="submit"
            className="btn btn-primary px-4"
            disabled={selectedDocs.length === 0 || loading || !query.trim()}
          >
            Send
          </button>
        </form>
        </div>
      </div>
    </div>
  </div>
);
}

export default App;
