import React, { useState } from 'react';

const MessageFormatter = ({ content, context, selectedDocs, openPdfPage }) => {
  const [isContextExpanded, setIsContextExpanded] = useState(true);

  const truncateContext = (text, maxLength = 200) => {
    if (!text) return "";
    if (text.length <= maxLength) return text;

    const truncated = text.substring(0, maxLength);
    const lastPeriod = truncated.lastIndexOf(".");
    const lastQuestion = truncated.lastIndexOf("?");
    const lastExclamation = truncated.lastIndexOf("!");

    const lastSentenceEnd = Math.max(lastPeriod, lastQuestion, lastExclamation);

    if (lastSentenceEnd > 0) {
      return text.substring(0, lastSentenceEnd + 1) + "...";
    }

    const lastSpace = truncated.lastIndexOf(" ");
    return text.substring(0, lastSpace) + "...";
  };

  const formatText = (text) => {
    if (!text) return '';

    let formattedText = text
      .replace(/^(\*{3})/g, '')
      .replace(/(\*\*|__)(.*?)\1/g, '<strong>$2</strong>')
      .replace(/(\*|_)(.*?)\1/g, '<em>$2</em>')
      .replace(/^(#{1,6})\s+(.+)$/gm, (match, hashes, content) => {
        const level = hashes.length;
        return `<h${level} class="text-${getHeaderSize(level)} font-bold my-3">${content}</h${level}>`;
      })
      .replace(/(\$?\d+(?:,\d{3})*(?:\.\d+)?(?:\s*(?:million|billion|trillion))?)/g, 
        '<span class="font-mono bg-blue-50 text-blue-700 px-1 rounded">$1</span>');

    return formattedText;
  };

  const getHeaderSize = (level) => {
    const sizes = {
      1: 'xl',
      2: 'lg',
      3: 'base',
      4: 'sm',
      5: 'xs',
      6: 'xs'
    };
    return sizes[level] || 'base';
  };

  const formatSection = (section) => {
    if (!section) return null;

    const lines = section.split('\n');
    const formattedLines = [];
    let currentList = [];

    lines.forEach((line, index) => {
      const trimmedLine = line.trim();

      if (trimmedLine.startsWith('***')) {
        const bulletContent = trimmedLine.substring(3).trim();
        currentList.push(bulletContent);
      } 
      else if (trimmedLine.startsWith('**')) {
        if (currentList.length > 0) {
          formattedLines.push(
            <ul key={`list-${index}`} className="list-disc pl-6 space-y-3">
              {currentList.map((item, idx) => (
                <li 
                  key={idx} 
                  className="mb-2 leading-relaxed"
                  dangerouslySetInnerHTML={{ __html: formatText(item) }}
                />
              ))}
            </ul>
          );
          currentList = [];
        }

        formattedLines.push(
          <p 
            key={`para-${index}`}
            className="mb-4 leading-relaxed"
            dangerouslySetInnerHTML={{ __html: formatText(trimmedLine) }}
          />
        );
      }
      else {
        if (currentList.length > 0) {
          formattedLines.push(
            <ul key={`list-${index}`} className="list-disc pl-6 space-y-3">
              {currentList.map((item, idx) => (
                <li 
                  key={idx} 
                  className="mb-2 leading-relaxed"
                  dangerouslySetInnerHTML={{ __html: formatText(item) }}
                />
              ))}
            </ul>
          );
          currentList = [];
        }

        if (trimmedLine) {
          formattedLines.push(
            <p 
              key={`para-${index}`}
              className="mb-4 leading-relaxed"
              dangerouslySetInnerHTML={{ __html: formatText(trimmedLine) }}
            />
          );
        }
      }
    });

    if (currentList.length > 0) {
      formattedLines.push(
        <ul key="final-list" className="list-disc pl-6 space-y-3">
          {currentList.map((item, idx) => (
            <li 
              key={idx} 
              className="mb-2 leading-relaxed"
              dangerouslySetInnerHTML={{ __html: formatText(item) }}
            />
          ))}
        </ul>
      );
    }

    return <>{formattedLines}</>;
  };

  const sections = content
    .split('\n\n')
    .filter(section => section.trim());

  return (
    <div className="message-content text-gray-800">
      {sections.map((section, idx) => (
        <div key={idx} className="mb-6">
          {formatSection(section)}
        </div>
      ))}

      {context && context.length > 0 && (
        <div className="mt-8 overflow-hidden rounded-xl bg-gradient-to-b from-gray-50 to-white border border-gray-100">
          <div 
            className="flex items-center justify-between p-4 cursor-pointer transition-colors duration-200 hover:bg-gray-50"
            onClick={() => setIsContextExpanded(!isContextExpanded)}
          >
            <h4 className="text-sm font-medium text-gray-600">Relevant Source Excerpts</h4>
            <div className="flex items-center gap-2">
              <span className="text-xs text-gray-400">{context.length} sources</span>
              <button 
                className={`text-gray-400 transition-transform duration-200 ${isContextExpanded ? 'rotate-180' : ''}`}
              >
                ▼
              </button>
            </div>
          </div>

          <div 
            className={`transition-all duration-200 ease-in-out ${
              isContextExpanded ? 'max-h-[1000px] opacity-100' : 'max-h-0 opacity-0'
            }`}
          >
            <div className="p-4 space-y-3">
              {context.map((ctx, idx) => {
                const relatedDoc = selectedDocs.find(d => d.id === ctx.documentId);
                return (
                  <div 
                    key={idx} 
                    className="bg-white rounded-lg border border-gray-100 shadow-sm transition-shadow duration-200 hover:shadow-md"
                  >
                    <div className="p-4">
                      <div className="flex justify-between items-start gap-4">
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-2">
                            <p className="text-sm font-medium text-gray-600">
                              {relatedDoc?.filename || `Document ${ctx.documentId}`}
                            </p>
                            <span className="inline-flex items-center px-2 py-1 rounded-full bg-blue-50 text-xs font-medium text-blue-600">
                              Page {ctx.page_number}
                            </span>
                          </div>
                          <p className="text-sm text-gray-600 leading-relaxed">
                            {truncateContext(ctx.content)}
                          </p>
                        </div>
                        <button
                          onClick={() => openPdfPage(relatedDoc?.file_url, ctx.page_number)}
                          className="flex items-center gap-1 px-3 py-1.5 text-xs font-medium text-blue-600 hover:text-blue-700 hover:bg-blue-50 rounded-lg transition-colors duration-200"
                        >
                          View
                        </button>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MessageFormatter;
