import React, { useState } from 'react';

const MessageFormatter = ({ content, context, selectedDocs, openPdfPage }) => {
  const [isContextExpanded, setIsContextExpanded] = useState(false);

  const truncateContext = (text, maxLength = 200) => {
    if (!text) return "";
    if (text.length <= maxLength) return text;

    const truncated = text.substring(0, maxLength);
    const lastPeriod = truncated.lastIndexOf(".");
    const lastQuestion = truncated.lastIndexOf("?");
    const lastExclamation = truncated.lastIndexOf("!");

    const lastSentenceEnd = Math.max(lastPeriod, lastQuestion, lastExclamation);

    if (lastSentenceEnd > 0) {
      return text.substring(0, lastSentenceEnd + 1) + "...";
    }

    const lastSpace = truncated.lastIndexOf(" ");
    return text.substring(0, lastSpace) + "...";
  };

  const formatText = (text) => {
    if (!text) return '';

    let formattedText = text
      .replace(/^(\*{3})/g, '')
      .replace(/(\*\*|__)(.*?)\1/g, '<strong>$2</strong>')
      .replace(/(\*|_)(.*?)\1/g, '<em>$2</em>')
      .replace(/^(#{1,6})\s+(.+)$/gm, (match, hashes, content) => {
        const level = hashes.length;
        return `<h${level} class="text-${getHeaderSize(level)} font-bold my-3">${content}</h${level}>`;
      })
      .replace(/(\$?\d+(?:,\d{3})*(?:\.\d+)?(?:\s*(?:million|billion|trillion))?)/g, 
        '<span class="font-mono bg-blue-50 text-blue-700 px-1 rounded">$1</span>');

    return formattedText;
  };

  const getHeaderSize = (level) => {
    const sizes = {
      1: 'xl',
      2: 'lg',
      3: 'base',
      4: 'sm',
      5: 'xs',
      6: 'xs'
    };
    return sizes[level] || 'base';
  };

  const formatSection = (section) => {
    if (!section) return null;
    const lines = section.split('\n');
    const formattedLines = [];
    let currentList = [];

    lines.forEach((line, index) => {
      const trimmedLine = line.trim();
      if (trimmedLine) {
        formattedLines.push(
          <p 
            key={`para-${index}`}
            className="mb-4"
            dangerouslySetInnerHTML={{ __html: formatText(trimmedLine) }}
          />
        );
      }
    });

    return <>{formattedLines}</>;
  };

  const sections = content
    .split('\n\n')
    .filter(section => section.trim());

  return (
    <div className="message-content text-gray-800 special">
      {sections.map((section, idx) => (
        <div key={idx} className="mb-4">
          {formatSection(section)}
        </div>
      ))}

      {context && context.length > 0 && (
        <div className="mt-4">
          <div 
            className="flex items-center justify-between cursor-pointer select-none"
            onClick={() => setIsContextExpanded(!isContextExpanded)}
          >
            <div className="flex items-center gap-2">
              <span className="text-xl font-semibold">Relevant Source Excerpts</span>
              <span className="text-gray-600">{context.length} sources</span>
            </div>
            <button 
              className="bg-gray-100 rounded w-6 h-6 flex items-center justify-center"
            >
              â–¾
            </button>
          </div>

          {isContextExpanded && (
            <div className="mt-4 space-y-4">
              {context.map((ctx, idx) => {
                const relatedDoc = selectedDocs.find(d => d.id === ctx.documentId);
                return (
                  <div key={idx} className="p-4 bg-gray-50 rounded">
                    <div className="flex justify-between items-start">
                      <div className="flex-1">
                        <div className="mb-2 text-sm text-gray-600">
                          {relatedDoc?.filename || `Document ${ctx.documentId}`}
                        </div>
                        <div className="text-sm">
                          {truncateContext(ctx.content)}
                        </div>
                      </div>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          openPdfPage(relatedDoc?.file_url, ctx.page_number);
                        }}
                        className="ml-4 px-3 py-1 text-sm border border-gray-200 rounded hover:bg-gray-100"
                      >
                        Page {ctx.page_number}
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default MessageFormatter;
